targets.expr:
(proc_pid_stat_state{instance=~"${instance}", hostname=~"${hostname}", tid=~${threads}} > 0)
* on (instance, hostname, pid) group_left(cmd, args)
proc_pid_cmdline{instance=~"${instance}", hostname=~"${hostname}", cmd=~"${cmd}"}

proc_pid_stat_pcpu{instance=~"${instance}",hostname=~"${hostname}",tid=~${threads}}
and on (instance, hostname, pid)
proc_pid_cmdline{instance=~"${instance}", hostname=~"${hostname}", cmd=~"${cmd}"}

proc_pid_stat_cpu_num{instance=~"${instance}",hostname=~"${hostname}",tid=~${threads}}
* on (instance, hostname, pid) group_left
proc_pid_cmdline{instance=~"${instance}", hostname=~"${hostname}", cmd=~"${cmd}"}

proc_pid_stat_rss_bytes{instance=~"${instance}",hostname=~"${hostname}"}
* on (instance, hostname, pid)
proc_pid_cmdline{instance=~"${instance}", hostname=~"${hostname}", cmd=~"${cmd}"}

max_over_time(proc_pid_stat_pcpu{instance=~"${instance}",hostname=~"${hostname}",tid=~${threads}}[${max_interval}s])
and on (instance, hostname, pid)
proc_pid_cmdline{instance=~"${instance}", hostname=~"${hostname}", cmd=~"${cmd}"}

(sum_over_time(proc_pid_stat_pcpu{instance=~"${instance}",hostname=~"${hostname}",tid=~${threads}}[${full_cycle}s])/${full_cycle})
and on (instance, hostname, pid)
proc_pid_cmdline{instance=~"${instance}", hostname=~"${hostname}", cmd=~"${cmd}"}
