proc_diskstats_num_reads_completed_delta{
  instance="${instance}",hostname="${hostname}",maj_min=~"${maj_min}",name=~"${disk_name}"
} >= ${delta_threshold}
and on (instance, hostname, maj_min)
(
  last_over_time(proc_mountinfo{instance="${instance}",hostname="${hostname}"}[${lookback}]) * ${mounted_only}
